import './customer-coupons.css';
import CustomerCouponsSidebar from './Sidebar/CustomerCouponsSidebar';
import AdminService from '../../../../../service/AdminService';
import AutenticationService from "../../../../../service/AutenticationService";
import { useState, useEffect } from 'react';
import CustomerService from '../../../../../service/CustomerService';



export default function CustomerCoupons() {

    const [coupons, setCoupons] = useState([{
        id: '',
        category: '',
        title: '',
        description: '',
        startDate: '',
        endDate: '',
        amount: '',
        price: '',
        image: '',
    }])


    useEffect(() => {

        CustomerService.getCoupons().then(
            response => {
                setCoupons(response.data);
            },
            (error) => {
                try {
                    if (error.response.data.string) {
                        return;
                    }
                    if (error.response) {
                        alert("Login expired, please login again.");
                        AutenticationService.logOut();
                    }
                } catch {
                    alert("Servers are down, please try again later.");
                    AutenticationService.logOut();
                }
            }
        )
    }, [])

    const getCoupons = (handleChange) => {
        if (coupons[0].id === '')
            return
        return (
            coupons.map((interval, index) => {
                return (
                    <tr key={index}>
                        <td>{interval.category}</td>
                        <td>{interval.title}</td>
                        <td>{interval.description}</td>
                        <td>{interval.startDate}</td>
                        <td>{interval.endDate}</td>
                        <td>{interval.amount}</td>
                        <td>{interval.price}</td>
                        <td>{interval.image}</td>
                    </tr>
                )
            })
        )
    }


    return (
        <div className="CustomerCoupons">
            <CustomerCouponsSidebar />
            <table className="table">
                <tbody>
                    <tr>
                        <th width="12.5%">category</th>
                        <th width="12.5%">title</th>
                        <th width="12.5%">description</th>
                        <th width="12.5%">Release Date</th>
                        <th width="12.5%">Expiration Date</th>
                        <th width="12.5%">Stock</th>
                        <th width="12.5%">price</th>
                        <th width="12.5%">(image)</th>
                    </tr>
                    {getCoupons(handleChange)}
                </tbody>
            </table>

        </div>
    )

}
