import { Field, Formik, Form, ErrorMessage } from 'formik'
import { values } from 'lodash'
import './login.css'
import history from '../history';
import * as Yup from "yup";
const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email format').required('Required'),
    password: Yup.string().required('Required')
})
export default function Login() {
    const handleSubmit = (values, { setFieldValue }) => {
        console.log(values)
        setFieldValue('password', "")
    }
    return (
        <Formik
            initialValues={{ email: "", password: "", type: "ADMIN" }}
            onSubmit={(values, { setFieldValue }) => handleSubmit(values, { setFieldValue })}
            validationSchema={validationSchema}
        >
            {({ isValid, dirty, values }) => (
                <Form>
                    <div>
                        <label>Enter your Email </label>
                        <Field
                            type="email"
                            name="email"
                            placeholder="Enter your Email"
                            title={values.email}
                            required={true}
                            autocomplete={true}
                        />

                    </div>
                    <ErrorMessage component='div' name='email' className='alert alert-primary' />


                    <div>
                        <label>Enter your Password </label>
                        <Field
                            type="password"
                            name="password"
                            placeholder="Enter your Password"
                            required={true}
                            autocomplete={true}
                        />
                    </div>
                    <ErrorMessage component='div' name='password' className='alert alert-primary' />


                    <div>
                        <label>Type </label>
                        <Field
                            component="select"
                            name="type"
                            placeholder="Enter your type"
                            title={values.type}
                            required={true}
                            autocomplete={true}>
                            <option value="ADMIN">Admin</option>
                            <option value="COMPANY">Company</option>
                            <option value="CUSTOMER">Customer</option>
                        </Field>
                    </div>
                    <button type="submit" disabled={!(isValid && dirty)} title="Login">
                        Login
                    </button>

                </Form>
            )}
        </Formik >
    )

}
