import AdminService from '../../../../../service/AdminService';
import AutenticationService from "../../../../../service/AutenticationService";
import { useState, useEffect } from 'react';
import { Formik, Form } from 'formik'
import CustomerPurchaseSidebar from './SideBar/SideBar';
import './customer-purchase.css'

export default function CustomerPurchase() {

  const [coupons, setCoupons] = useState([{
    id: '',
    category: '',
    title: '',
    description: '',
    startDate: '',
    endDate: '',
    amount: '',
    price: '',
    image: '',
  }])

  useEffect(() => {

    AdminService.getCoupons().then(
      response => {
        setCoupons(response.data);
      },
      (error) => {
        try {
          if (error.response.data.string) {
            alert(error.response.data.string);
            return;
          }
          if (error.response) {
            alert("Login expired, please login again.");
            AutenticationService.logOut();
          }
        } catch {
          alert("Servers are down, please try again later.");
          AutenticationService.logOut();
        }
      }
    )
  }, [])

  const getCoupons = (handleChange) => {
    if (coupons[0].id === '')
      return
    return (
      coupons.map((interval, index) => {
        return (
          <tr key={index}>
            <td><input onChange={handleChange} type="checkbox" name="checked" value={interval.id} /></td>
            <td>{interval.category}</td>
            <td>{interval.title}</td>
            <td>{interval.description}</td>
            <td>{interval.startDate}</td>
            <td>{interval.endDate}</td>
            <td>{interval.amount}</td>
            <td>{interval.price}</td>
            <td>{interval.image}</td>
          </tr>
        )
      })
    )
  }

  return (
    <div className="CustomerPurchase">
      <Formik

        initialValues={{ checked: [] }}
      >
        {({ handleChange, values }) => (
          <Form >
            <CustomerPurchaseSidebar values={values} />
            <table className="table">
              <tbody>
                <tr>
                  <th width="5%">(Checkbox)</th>
                  <th width="10%">category</th>
                  <th width="10%">title</th>
                  <th width="10%">description</th>
                  <th width="10%">Release Date</th>
                  <th width="10%">Expiration Date</th>
                  <th width="10%">Stock</th>
                  <th width="10%">price</th>
                  <th width="10%">(image)</th>
                </tr>
                {getCoupons(handleChange)}
              </tbody>
            </table>
          </Form>
        )}
      </Formik >




    </div>
  )
}
